      "overview": "Closest chains for tape, blister care, sunscreen, and quick scripts—minimal detour from Avenue 58/Polk and CA-111 corridors.",
      "items": [
        {
          "name": "CVS Pharmacy – Indio",
          "approx_drive_min": "12–18",
          "services": "Rx, retail, many with drive-thru",
          "parking": "Surface lot",
          "notes": "Fast grab pre-jog for ice packs and electrolyte tabs."
        },
        {
          "name": "Walgreens – La Quinta/Indio",
          "approx_drive_min": "12–20",
          "services": "Rx, photo, drive-thru (many sites)",
          "parking": "Surface lot",
          "notes": "Reliable late-evening stop."
        },
        {
          "name": "Rite Aid – Indio area",
          "approx_drive_min": "15–22",
          "services": "Rx, retail",
          "parking": "Surface lot",
          "notes": "Backup option if the main chains are slammed."
        }
      ]
    },
    "feed_tack_bedding": {
      "overview": "Use on-grounds delivery when you’re ring-bound; nearby ranch retailers for emergency top-ups. Call ahead for pallet loads and grain brands.",
      "items": [
        {
          "name": "On-grounds Feed & Bedding (via Show Office / Vendor Row)",
          "approx_drive_min": "On-site",
          "services": "Stall delivery: shavings, pellets, hay",
          "parking": "N/A",
          "notes": "Order by late morning to avoid afternoon delivery backlogs near vendor row."
        },
        {
          "name": "Tractor Supply Co. – Indio/Coachella corridor",
          "approx_drive_min": "18–25",
          "services": "Shavings, buckets, hardware, some feeds",
          "parking": "Trailer-friendly",
          "notes": "Quick fix for muck tools, tubs, and extras."
        },
        {
          "name": "Regional Hay Supplier – Delivery to Venue",
          "approx_drive_min": "Delivered",
          "services": "Timothy/alfalfa/coastal (varies), bulk orders",
          "parking": "On-grounds drop",
          "notes": "Confirm cut/type and delivery windows on feature-class days."
        },
        {
          "name": "Local Tack Outlets – La Quinta/Palm Desert",
          "approx_drive_min": "20–35",
          "services": "Bits, gloves, spur straps, show shirts",
          "parking": "Plaza lot",
          "notes": "Handy for last-second apparel and leather care."
        }
      ],
      "pro_tip": "Keep a two-day reserve of shavings and preferred grain by Thursday—phones light up before weekend grands prix."
    },
    "car_rental": {
      "overview": "Local branches for mid-week swaps; PSP airport for the strongest fleet and extended hours.",
      "items": [
        {
          "name": "Enterprise Rent-A-Car – La Quinta/Indio",
          "approx_drive_min": "15–25",
          "chains": "Enterprise",
          "parking": "Plaza lot",
          "notes": "Ask about Sunday hours and after-hours key drop."
        },
        {
          "name": "Palm Springs International Airport (PSP)",
          "approx_drive_min": "35–50",
          "chains": "Avis, Budget, Hertz, National, Alamo, more",
          "parking": "Airport rental center",
          "notes": "Best inventory for late arrivals and one-way swaps."
        },
        {
          "name": "Ontario International Airport (ONT)",
          "approx_drive_min": "75–100",
          "chains": "Major brands",
          "parking": "Airport rental facility",
          "notes": "Fallback if PSP is sold out during headline weeks."
        }
      ]
    },
    "golf_cart_rental": {
      "overview": "Book early for feature weeks; confirm charging near your stabling row and request rain enclosures for night classes.",
      "items": [
        {
          "name": "On-grounds Golf Cart Rentals (via Show Office)",
          "approx_drive_min": "On-site",
          "services": "Daily/weekly rentals, on-grounds service",
          "parking": "N/A",
          "notes": "Clarify approved cart paths around warm-up rings and jog strip."
        },
        {
          "name": "Coachella Valley Golf Cart Provider – Delivery to Venue",
          "approx_drive_min": "Delivered",
          "services": "Multi-week rentals, drop/pick at stabling",
          "parking": "N/A",
          "notes": "Request two keys—one for groom runs to vendor row."
        }
      ],
      "pro_tip": "Schedule delivery during ticketed schooling to avoid barn-aisle gridlock and ring changeovers."
    }
  }
}

