      "overview": "Airport center = best inventory and one-way flexibility; neighborhood branches for quick mid-week swaps.",
      "items": [
        {
          "name": "Harrisburg International Airport (MDT) – Rental Car Center",
          "approx_drive_min": "20–25",
          "chains": "Hertz, Avis, National, Alamo, Budget",
          "parking": "Structured/center lots",
          "notes": "Reserve small SUV for coolers and trunks; check for toll pass options."
        },
        {
          "name": "Enterprise Rent-A-Car – Harrisburg/Cameron–Paxton corridor",
          "approx_drive_min": "10–15",
          "chains": "Enterprise",
          "parking": "On-site lot",
          "notes": "Ask about after-hours key drop if you’re showing Sunday."
        }
      ]
    },
    "golf_cart_rental": {
      "overview": "Prefer delivery to barn rows; request rain enclosures and a utility bed for bedding/ice runs.",
      "items": [
        {
          "name": "Regional Golf Cart Provider – Delivery to venue",
          "approx_drive_min": "Delivered",
          "services": "Short-term rentals, drop-off/pickup",
          "parking": "N/A",
          "notes": "Book 1–2 weeks ahead for peak weeks; verify charger location and extra key sets."
        },
        {
          "name": "On-grounds Cart Vendor (if available)",
          "approx_drive_min": "On-site",
          "services": "Daily/weekly rentals",
          "parking": "N/A",
          "notes": "Reserve through show office; confirm insurance and speed rules near the jog strip."
        }
      ],
      "pro_tip": "Align delivery with ticketed schooling to keep aisleways clear and avoid gate queues."
    }
  }
