        },
        {
          "name": "Ingles Pharmacy (inside Ingles, select locations)",
          "approx_drive_min": "12–18",
          "services": "In-store pharmacy",
          "parking": "Large lot",
          "notes": "One-stop for rider meds plus barn snacks."
        }
      ]
    },
