        {
          "name": "On-grounds Bedding/Feed Vendor (show weeks)",
          "approx_drive_min": "On-site delivery",
          "services": "Delivery straight to stabling lanes",
          "parking": "N/A",
          "notes": "Book through the show office; best drop window mid-morning when vendor row is quiet."
        }
      ],
      "pro_tip": "Stage a reserve stack behind your stalls before the weekend—afternoon restocks can bottleneck once schooling and ticketed warm-ups overlap."
    },
    "car_rental": {
      "overview": "Small-town branches are convenient mid-week; airports have the best fleet selection. SUVs handle coolers and trunks best.",
      "items": [
        {
          "name": "Enterprise Rent-A-Car (nearby towns)",
          "approx_drive_min": "20–35",
          "chains": "Enterprise",
          "parking": "On-site lot",
          "notes": "Ask about after-hours key drop for late Sunday wraps."
        },
        {
          "name": "Greenville-Spartanburg Intl. (GSP) Rental Car Center",
          "approx_drive_min": "50–65",
          "chains": "Hertz, Avis, National, Alamo, Budget (varies)",
          "parking": "Structured/center lots",
          "notes": "Best inventory; confirm toll and additional driver policies."
        },
        {
          "name": "Asheville Regional (AVL) Rental Car Center",
          "approx_drive_min": "55–70",
          "chains": "Major brands (varies)",
          "parking": "On-airport lots",
          "notes": "Good backup if GSP is tight during peak weeks."
        }
      ]
    },
    "golf_cart_rental": {
      "overview": "Order early for rain enclosures and a utility bed; verify charging close to your stabling row and bring a heavy-duty extension cord.",
      "items": [
        {
          "name": "On-grounds Cart Vendor (if offered)",
          "approx_drive_min": "On-site",
          "services": "Daily/weekly rentals via show office",
          "parking": "N/A",
          "notes": "Reserve at the same time as stalls; confirm insurance and gate speed rules near the jog strip."
        },
        {
          "name": "Regional Golf Cart Provider – Delivery",
          "approx_drive_min": "Delivered",
          "services": "Short-term rentals, drop-off/pickup to venue",
          "parking": "N/A",
          "notes": "Book 1–2 weeks ahead; request extra key sets for grooms and runners."
        }
      ],
      "pro_tip": "Schedule delivery during ticketed schooling to avoid back-ups at the barn aisles and ring gates."
    }
  }
}

